sys_script_client.script:
function onLoad() {
    if (g_form.isLoading()) {
        return; // Form loading hai to kuch nahi karna
    }

    if (g_form.isNewRecord()) {
        var requestedFor = g_form.getValue('requested_for'); // "Requested for" field ka value lein
        if (requestedFor) {
            var email = getEmailForUser(requestedFor); // Email fetch karne ka function call karein
            g_form.setValue('requested_for_email', email); // Email field ko set karein
        }
    }
}

function getEmailForUser(userId) {
    var user = new GlideRecord('sys_user'); // User table ko access karna
    if (user.get(userId)) {
        return user.email; // Email return karein
    }
    return ''; // Agar user nahi mila to khali return karein
}
